import React, { useEffect, useRef, useState } from 'react';

import './App.css';
import axios from 'axios';


function App() {

  const [Mobile, setMobile] = useState("09115755339");
  const [Token, setToken ]= useState("");
  const inputRefMobile = useRef(null);


  const Login = async () => {
    setMobile(inputRefMobile.current.value);
    const  RequestAuthorizeLogin = {
      Mobile: Mobile
    };
    const url=  " https://localhost:7079/Api/Authentication/Login";

    axios.post( url,    RequestAuthorizeLogin )
      .then(res => {

        setToken(res.data.token);
      })

  };

  useEffect(() => {

  }, []);

  return (
    <div className="App">

      <br/>
      <input type="text" value={Mobile} placeholder='Mobile'   id="mobile"
        name="mobile"  ref={inputRefMobile} />
      <input type="button" value="Token Insert" onClick={Login} /> 
      <br/>    <br/>
      <input type="text" value={Token} placeholder='Token' />
    </div>
  );
}

export default App;
